import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, 
  Typography, 
  CircularProgress, 
  Alert,
  Container,
  Paper,
  Grid,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import AddIcon from '@mui/icons-material/Add';
import api from '../api/axios';

const AbsencesPage = () => {
  const [absences, setAbsences] = useState([]);
  const [formations, setFormations] = useState([]);
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [selectedAbsence, setSelectedAbsence] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const [formData, setFormData] = useState({
    id: null,
    formation_id: '',
    participant_id: '',
    date: '',
    motif: '',
    justifiee: false
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Dans un vrai projet, ces appels API seraient réels
      // Pour la démo, on utilise des données fictives
      
      // Simuler des données de formations
      const mockFormations = [
        { id: 1, titre: 'Formation JavaScript' },
        { id: 2, titre: 'Formation React' },
        { id: 3, titre: 'Architecture MVC' }
      ];
      
      // Simuler des données de participants
      const mockParticipants = [
        { id: 1, nom: 'Dupont', prenom: 'Simon' },
        { id: 2, nom: 'Martin', prenom: 'Julie' },
        { id: 3, nom: 'Leblanc', prenom: 'Thomas' }
      ];
      
      // Simuler des données d'absences
      const mockAbsences = [
        { 
          id: 1, 
          formation_id: 1, 
          formation_titre: 'Formation JavaScript',
          participant_id: 1,
          participant_nom: 'Dupont Simon',
          date: '2023-10-05',
          motif: 'Maladie',
          justifiee: true
        },
        { 
          id: 2, 
          formation_id: 1,
          formation_titre: 'Formation JavaScript',
          participant_id: 2,
          participant_nom: 'Martin Julie',
          date: '2023-10-07',
          motif: 'Rendez-vous médical',
          justifiee: true
        },
        { 
          id: 3, 
          formation_id: 2,
          formation_titre: 'Formation React',
          participant_id: 3,
          participant_nom: 'Leblanc Thomas',
          date: '2023-11-10',
          motif: 'Problème de transport',
          justifiee: false
        }
      ];
      
      setFormations(mockFormations);
      setParticipants(mockParticipants);
      setAbsences(mockAbsences);
    } catch (error) {
      console.error("Error fetching data:", error);
      setError(`Erreur: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = (absence = null) => {
    if (absence) {
      // Mode édition
      setIsEditing(true);
      setFormData({
        id: absence.id,
        formation_id: absence.formation_id,
        participant_id: absence.participant_id,
        date: absence.date,
        motif: absence.motif,
        justifiee: absence.justifiee
      });
    } else {
      // Mode ajout
      setIsEditing(false);
      setFormData({
        id: null,
        formation_id: '',
        participant_id: '',
        date: '',
        motif: '',
        justifiee: false
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleDeleteClick = (absence) => {
    setSelectedAbsence(absence);
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
  };

  const handleConfirmDelete = async () => {
    try {
      // Dans un vrai projet, appel API pour supprimer
      // await api.delete(`/absences/${selectedAbsence.id}`);
      
      // Mise à jour de l'état local
      setAbsences(absences.filter(a => a.id !== selectedAbsence.id));
      setOpenDeleteDialog(false);
    } catch (error) {
      console.error("Error deleting absence:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    
    if (name === 'justifiee') {
      setFormData(prev => ({ ...prev, [name]: checked }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        // Dans un vrai projet, appel API pour modifier
        // await api.put(`/absences/${formData.id}`, formData);
        
        // Mise à jour de l'état local
        setAbsences(absences.map(a => a.id === formData.id ? {
          ...formData,
          formation_titre: formations.find(f => f.id === parseInt(formData.formation_id))?.titre,
          participant_nom: participants.find(p => p.id === parseInt(formData.participant_id))
            ? `${participants.find(p => p.id === parseInt(formData.participant_id)).nom} ${participants.find(p => p.id === parseInt(formData.participant_id)).prenom}`
            : ''
        } : a));
      } else {
        // Dans un vrai projet, appel API pour ajouter
        // const response = await api.post('/absences', formData);
        
        // Simuler un nouvel ID pour la démo
        const newId = Math.max(0, ...absences.map(a => a.id)) + 1;
        
        // Mise à jour de l'état local
        setAbsences([...absences, {
          ...formData,
          id: newId,
          formation_titre: formations.find(f => f.id === parseInt(formData.formation_id))?.titre,
          participant_nom: participants.find(p => p.id === parseInt(formData.participant_id))
            ? `${participants.find(p => p.id === parseInt(formData.participant_id)).nom} ${participants.find(p => p.id === parseInt(formData.participant_id)).prenom}`
            : ''
        }]);
      }
      
      setOpenDialog(false);
    } catch (error) {
      console.error("Error saving absence:", error);
    }
  };

  if (loading) return (
    <Box textAlign="center" mt={4}>
      <CircularProgress />
      <Typography mt={2}>Chargement des absences...</Typography>
    </Box>
  );
  
  if (error) return (
    <Box mt={4} p={2}>
      <Alert severity="error">{error}</Alert>
      <Button variant="contained" color="primary" onClick={fetchData} sx={{ mt: 2 }}>
        Réessayer
      </Button>
    </Box>
  );

  return (
    <Container maxWidth="lg">
      <Box mb={4} mt={2}>
        <Button 
          variant="outlined" 
          startIcon={<ArrowBackIcon />} 
          component={Link} 
          to="/formateur-animateur"
          sx={{ mb: 2 }}
        >
          Retour au profil
        </Button>
        
        <Typography variant="h4" component="h1" gutterBottom>
          Gestion des absences
        </Typography>
      </Box>

      {/* Liste des absences */}
      <Box mb={4}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
          <Typography variant="h5" component="h2">
            Liste des absences
          </Typography>
          <Button 
            variant="contained" 
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog()}
          >
            Ajouter une absence
          </Button>
        </Box>

        {absences.length === 0 ? (
          <Alert severity="info">Aucune absence enregistrée.</Alert>
        ) : (
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="table des absences">
              <TableHead>
                <TableRow>
                  <TableCell>Formation</TableCell>
                  <TableCell>Participant</TableCell>
                  <TableCell>Date</TableCell>
                  <TableCell>Motif</TableCell>
                  <TableCell>Justifiée</TableCell>
                  <TableCell align="center">Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {absences.map((absence) => (
                  <TableRow key={absence.id}>
                    <TableCell>{absence.formation_titre}</TableCell>
                    <TableCell>{absence.participant_nom}</TableCell>
                    <TableCell>{absence.date}</TableCell>
                    <TableCell>{absence.motif}</TableCell>
                    <TableCell>{absence.justifiee ? 'Oui' : 'Non'}</TableCell>
                    <TableCell align="center">
                      <IconButton 
                        color="secondary"
                        onClick={() => handleOpenDialog(absence)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton 
                        color="error"
                        onClick={() => handleDeleteClick(absence)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        )}
      </Box>

      {/* Dialog pour ajouter/modifier une absence */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <form onSubmit={handleSubmit}>
          <DialogTitle>{isEditing ? 'Modifier une absence' : 'Ajouter une absence'}</DialogTitle>
          <DialogContent>
            <Grid container spacing={2} sx={{ mt: 1 }}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="formation-label">Formation</InputLabel>
                  <Select
                    labelId="formation-label"
                    name="formation_id"
                    value={formData.formation_id}
                    onChange={handleChange}
                    label="Formation"
                    required
                  >
                    {formations.map(formation => (
                      <MenuItem key={formation.id} value={formation.id}>
                        {formation.titre}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="participant-label">Participant</InputLabel>
                  <Select
                    labelId="participant-label"
                    name="participant_id"
                    value={formData.participant_id}
                    onChange={handleChange}
                    label="Participant"
                    required
                  >
                    {participants.map(participant => (
                      <MenuItem key={participant.id} value={participant.id}>
                        {participant.nom} {participant.prenom}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              
              <Grid item xs={12}>
                <TextField
                  name="date"
                  label="Date"
                  type="date"
                  value={formData.date}
                  onChange={handleChange}
                  InputLabelProps={{ shrink: true }}
                  fullWidth
                  required
                />
              </Grid>
              
              <Grid item xs={12}>
                <TextField
                  name="motif"
                  label="Motif"
                  value={formData.motif}
                  onChange={handleChange}
                  fullWidth
                  multiline
                  rows={2}
                  required
                />
              </Grid>
              
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="justifiee-label">Justifiée</InputLabel>
                  <Select
                    labelId="justifiee-label"
                    name="justifiee"
                    value={formData.justifiee}
                    onChange={handleChange}
                    label="Justifiée"
                  >
                    <MenuItem value={true}>Oui</MenuItem>
                    <MenuItem value={false}>Non</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDialog}>Annuler</Button>
            <Button type="submit" variant="contained" color="primary">
              {isEditing ? 'Enregistrer' : 'Ajouter'}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Dialog de confirmation de suppression */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
      >
        <DialogTitle>Confirmation de suppression</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Êtes-vous sûr de vouloir supprimer cette absence ? Cette action est irréversible.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Annuler
          </Button>
          <Button onClick={handleConfirmDelete} color="error" variant="contained">
            Supprimer
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AbsencesPage;
