import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, 
  Typography, 
  CircularProgress, 
  Alert,
  Container,
  Paper,
  Grid,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Tooltip,
  Divider,
  DialogContentText,
  Stack,
  Card,
  CardContent,
  useTheme
} from '@mui/material';
import { styled } from '@mui/material/styles';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import DescriptionIcon from '@mui/icons-material/Description';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import TimerIcon from '@mui/icons-material/Timer';
import CategoryIcon from '@mui/icons-material/Category';
import api from '../api/axios';

// Stylisation personnalisée
const CustomContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(3),
}));

const CustomPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
  borderRadius: '12px',
  background: '#fff',
  marginBottom: theme.spacing(4),
  borderTop: '4px solid #B399D6',
}));

const CustomTableContainer = styled(TableContainer)(({ theme }) => ({
  boxShadow: 'none',
  borderRadius: '12px',
  overflow: 'hidden',
  '& .MuiTableCell-head': {
    backgroundColor: '#B399D6',
    color: '#fff',
    fontWeight: 'bold',
  },
  '& .MuiTableRow-root:nth-of-type(even)': {
    backgroundColor: '#f8f6fb',
  },
}));

const ActionButton = styled(IconButton)(({ theme, color }) => ({
  margin: theme.spacing(0.5),
  backgroundColor: color === 'primary' ? 'rgba(179, 153, 214, 0.1)' : 
                  color === 'secondary' ? 'rgba(73, 177, 189, 0.1)' : 
                  'rgba(239, 83, 80, 0.1)',
  '&:hover': {
    backgroundColor: color === 'primary' ? 'rgba(179, 153, 214, 0.2)' : 
                    color === 'secondary' ? 'rgba(73, 177, 189, 0.2)' : 
                    'rgba(239, 83, 80, 0.2)',
  },
}));

const TypeChip = styled(Chip)(({ type }) => ({
  fontWeight: 'bold',
  backgroundColor: type === 'Quiz' ? 'rgba(179, 153, 214, 0.2)' : 
                  type === 'Activité' ? 'rgba(255, 193, 7, 0.2)' : 
                  'rgba(76, 175, 80, 0.2)',
  color: type === 'Quiz' ? '#8E65C0' : 
        type === 'Activité' ? '#F57C00' : 
        '#388E3C',
}));

const StyledButton = styled(Button)(({ theme, variant }) => ({
  borderRadius: '8px',
  padding: variant === 'contained' ? '8px 24px' : '7px 24px',
  fontWeight: 'bold',
  textTransform: 'none',
  boxShadow: variant === 'contained' ? '0 4px 10px rgba(179, 153, 214, 0.3)' : 'none',
  backgroundColor: variant === 'contained' ? '#B399D6' : 'transparent',
  borderColor: variant === 'outlined' ? '#B399D6' : 'transparent',
  color: variant === 'contained' ? '#fff' : '#B399D6',
  '&:hover': {
    backgroundColor: variant === 'contained' ? '#A283C6' : 'rgba(179, 153, 214, 0.1)',
    borderColor: variant === 'outlined' ? '#A283C6' : 'transparent',
    boxShadow: variant === 'contained' ? '0 6px 12px rgba(179, 153, 214, 0.4)' : 'none',
  },
}));

const EvaluationList = () => {
  const [evaluations, setEvaluations] = useState([]);
  const [formations, setFormations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [selectedEvaluation, setSelectedEvaluation] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const [formData, setFormData] = useState({
    id: null,
    formation_id: '',
    titre: '',
    type: '',
    date: '',
    duree: '',
    description: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Dans un vrai projet, ces appels API seraient réels
      // Pour la démo, on utilise des données fictives

      // Simuler des données de formations
      const mockFormations = [
        { id: 1, titre: 'Formation JavaScript' },
        { id: 2, titre: 'Formation React' },
        { id: 3, titre: 'Architecture MVC' }
      ];

      // Simuler des données d'évaluations
      const mockEvaluations = [
        { 
          id: 1, 
          formation_id: 1, 
          formation_titre: 'Formation JavaScript',
          titre: 'Quiz sur les fondamentaux',
          type: 'Quiz',
          date: '2023-10-10',
          duree: 30,
          description: 'Quiz sur les concepts de base de JavaScript'
        },
        { 
          id: 2, 
          formation_id: 1,
          formation_titre: 'Formation JavaScript',
          titre: 'Examen final',
          type: 'Examen',
          date: '2023-10-15',
          duree: 120,
          description: 'Examen de validation des acquis'
        },
        { 
          id: 3, 
          formation_id: 2,
          formation_titre: 'Formation React',
          titre: 'Travaux pratiques',
          type: 'Activité',
          date: '2023-11-20',
          duree: 90,
          description: 'Création d\'un composant React'
        }
      ];

      setFormations(mockFormations);
      setEvaluations(mockEvaluations);
    } catch (error) {
      console.error("Error fetching data:", error);
      setError(`Erreur: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = (evaluation = null) => {
    if (evaluation) {
      // Mode édition
      setIsEditing(true);
      setFormData({
        id: evaluation.id,
        formation_id: evaluation.formation_id,
        titre: evaluation.titre,
        type: evaluation.type,
        date: evaluation.date,
        duree: evaluation.duree,
        description: evaluation.description || ''
      });
    } else {
      // Mode ajout
      setIsEditing(false);
      setFormData({
        id: null,
        formation_id: '',
        titre: '',
        type: '',
        date: '',
        duree: '',
        description: ''
      });
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        // Dans un vrai projet, appel API pour modifier
        // const response = await api.put(`/evaluations/${formData.id}`, formData);

        // Mise à jour de l'état local
        setEvaluations(evaluations.map(e => 
          e.id === formData.id 
            ? {
                ...formData,
                formation_titre: formations.find(f => f.id === parseInt(formData.formation_id))?.titre
              } 
            : e
        ));
      } else {
        // Dans un vrai projet, appel API pour ajouter
        // const response = await api.post('/evaluations', formData);

        // Simuler un nouvel ID pour la démo
        const newId = Math.max(0, ...evaluations.map(e => e.id)) + 1;

        // Mise à jour de l'état local
        setEvaluations([...evaluations, {
          ...formData,
          id: newId,
          formation_titre: formations.find(f => f.id === parseInt(formData.formation_id))?.titre
        }]);
      }

      setOpenDialog(false);
    } catch (error) {
      console.error("Error saving evaluation:", error);
    }
  };

  const handleOpenDeleteDialog = (evaluation) => {
    setSelectedEvaluation(evaluation);
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
  };

  const handleDeleteEvaluation = () => {
    try {
      // Dans un vrai projet, appel API pour supprimer
      // await api.delete(`/evaluations/${selectedEvaluation.id}`);

      // Mise à jour de l'état local
      setEvaluations(evaluations.filter(e => e.id !== selectedEvaluation.id));
      setOpenDeleteDialog(false);
    } catch (error) {
      console.error("Error deleting evaluation:", error);
    }
  };

  // Formatter la date pour l'affichage
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  if (loading) return (
    <Box textAlign="center" mt={4} sx={{ color: '#B399D6' }}>
      <CircularProgress sx={{ color: '#B399D6' }} />
      <Typography mt={2}>Chargement des évaluations...</Typography>
    </Box>
  );

  if (error) return (
    <Box mt={4} p={2}>
      <Alert severity="error">{error}</Alert>
      <StyledButton variant="contained" onClick={fetchData} sx={{ mt: 2 }}>
        Réessayer
      </StyledButton>
    </Box>
  );

  return (
    <CustomContainer maxWidth="lg">
      <Box mb={4} mt={2}>
        <StyledButton 
          variant="outlined" 
          startIcon={<ArrowBackIcon />} 
          component={Link} 
          to="/formateur-animateur"
          sx={{ mb: 2 }}
        >
          Retour au profil
        </StyledButton>

        <Typography variant="h4" component="h1" gutterBottom sx={{ color: '#333', fontWeight: 'bold' }}>
          Gestion des évaluations
        </Typography>
      </Box>

      {/* Liste des évaluations */}
      <CustomPaper>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h5" component="h2" sx={{ color: '#B399D6', fontWeight: 'bold' }}>
            Liste des évaluations
          </Typography>
          <StyledButton 
            variant="contained" 
            startIcon={<AddIcon />}
            onClick={() => handleOpenDialog()}
          >
            Ajouter une évaluation
          </StyledButton>
        </Box>

        {evaluations.length === 0 ? (
          <Alert severity="info" sx={{ backgroundColor: 'rgba(179, 153, 214, 0.1)', color: '#B399D6' }}>
            Aucune évaluation enregistrée.
          </Alert>
        ) : (
          <>
            {/* Vue desktop: tableau */}
            <Box sx={{ display: { xs: 'none', md: 'block' } }}>
              <CustomTableContainer component={Paper}>
                <Table sx={{ minWidth: 650 }} aria-label="table des évaluations">
                  <TableHead>
                    <TableRow>
                      <TableCell>Formation</TableCell>
                      <TableCell>Titre</TableCell>
                      <TableCell>Type</TableCell>
                      <TableCell>Date</TableCell>
                      <TableCell>Durée</TableCell>
                      <TableCell align="center">Actions</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {evaluations.map((evaluation) => (
                      <TableRow key={evaluation.id} hover>
                        <TableCell>{evaluation.formation_titre}</TableCell>
                        <TableCell>
                          <Typography variant="body1" fontWeight="medium">
                            {evaluation.titre}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <TypeChip 
                            label={evaluation.type} 
                            type={evaluation.type}
                            size="small"
                          />
                        </TableCell>
                        <TableCell>{formatDate(evaluation.date)}</TableCell>
                        <TableCell>{evaluation.duree} min</TableCell>
                        <TableCell align="center">
                          <Box>
                            <Tooltip title="Voir les détails">
                              <ActionButton 
                                color="primary"
                                component={Link}
                                to={`/evaluations/${evaluation.id}`}
                                size="small"
                              >
                                <VisibilityIcon fontSize="small" sx={{ color: '#B399D6' }} />
                              </ActionButton>
                            </Tooltip>
                            <Tooltip title="Modifier">
                              <ActionButton 
                                color="secondary"
                                onClick={() => handleOpenDialog(evaluation)}
                                size="small"
                              >
                                <EditIcon fontSize="small" sx={{ color: '#49B1BD' }} />
                              </ActionButton>
                            </Tooltip>
                            <Tooltip title="Supprimer">
                              <ActionButton 
                                color="error"
                                onClick={() => handleOpenDeleteDialog(evaluation)}
                                size="small"
                              >
                                <DeleteIcon fontSize="small" sx={{ color: '#EF5350' }} />
                              </ActionButton>
                            </Tooltip>
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </CustomTableContainer>
            </Box>

            {/* Vue mobile: cartes */}
            <Box sx={{ display: { xs: 'block', md: 'none' } }}>
              <Stack spacing={2}>
                {evaluations.map((evaluation) => (
                  <Card key={evaluation.id} sx={{ borderRadius: '10px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)' }}>
                    <CardContent>
                      <Box display="flex" justifyContent="space-between" alignItems="center" mb={1}>
                        <Typography variant="h6" fontWeight="bold" color="#333">
                          {evaluation.titre}
                        </Typography>
                        <TypeChip 
                          label={evaluation.type} 
                          type={evaluation.type}
                          size="small"
                        />
                      </Box>

                      <Typography variant="body2" color="text.secondary" mb={1}>
                        {evaluation.formation_titre}
                      </Typography>

                      <Box display="flex" alignItems="center" gap={2} mt={1} mb={2}>
                        <Box display="flex" alignItems="center">
                          <CalendarTodayIcon fontSize="small" sx={{ color: '#B399D6', mr: 0.5 }} />
                          <Typography variant="body2">{formatDate(evaluation.date)}</Typography>
                        </Box>
                        <Box display="flex" alignItems="center">
                          <TimerIcon fontSize="small" sx={{ color: '#B399D6', mr: 0.5 }} />
                          <Typography variant="body2">{evaluation.duree} min</Typography>
                        </Box>
                      </Box>

                      <Box display="flex" justifyContent="flex-end" gap={1}>
                        <ActionButton 
                          color="primary"
                          component={Link}
                          to={`/evaluations/${evaluation.id}`}
                          size="small"
                        >
                          <VisibilityIcon fontSize="small" sx={{ color: '#B399D6' }} />
                        </ActionButton>
                        <ActionButton 
                          color="secondary"
                          onClick={() => handleOpenDialog(evaluation)}
                          size="small"
                        >
                          <EditIcon fontSize="small" sx={{ color: '#49B1BD' }} />
                        </ActionButton>
                        <ActionButton 
                          color="error"
                          onClick={() => handleOpenDeleteDialog(evaluation)}
                          size="small"
                        >
                          <DeleteIcon fontSize="small" sx={{ color: '#EF5350' }} />
                        </ActionButton>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Stack>
            </Box>
          </>
        )}
      </CustomPaper>

      {/* Dialog pour ajouter/modifier une évaluation */}
      <Dialog open={openDialog} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <form onSubmit={handleSubmit}>
          <DialogTitle sx={{ bgcolor: '#B399D6', color: '#fff' }}>
            {isEditing ? 'Modifier l\'évaluation' : 'Ajouter une évaluation'}
          </DialogTitle>
          <DialogContent dividers>
            <Grid container spacing={2} sx={{ mt: 0.5 }}>
              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="formation-label">Formation</InputLabel>
                  <Select
                    labelId="formation-label"
                    name="formation_id"
                    value={formData.formation_id}
                    onChange={handleChange}
                    label="Formation"
                    required
                    sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                  >
                    {formations.map(formation => (
                      <MenuItem key={formation.id} value={formation.id}>
                        {formation.titre}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  name="titre"
                  label="Titre"
                  value={formData.titre}
                  onChange={handleChange}
                  fullWidth
                  required
                  sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                />
              </Grid>

              <Grid item xs={12}>
                <FormControl fullWidth>
                  <InputLabel id="type-label">Type</InputLabel>
                  <Select
                    labelId="type-label"
                    name="type"
                    value={formData.type}
                    onChange={handleChange}
                    label="Type"
                    required
                    sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                  >
                    <MenuItem value="Quiz">Quiz</MenuItem>
                    <MenuItem value="Activité">Activité</MenuItem>
                    <MenuItem value="Examen">Examen</MenuItem>
                  </Select>
                </FormControl>
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  name="date"
                  label="Date"
                  type="date"
                  value={formData.date}
                  onChange={handleChange}
                  InputLabelProps={{ shrink: true }}
                  fullWidth
                  required
                  sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  name="duree"
                  label="Durée (minutes)"
                  type="number"
                  value={formData.duree}
                  onChange={handleChange}
                  fullWidth
                  required
                  inputProps={{ min: 1 }}
                  sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  name="description"
                  label="Description"
                  value={formData.description}
                  onChange={handleChange}
                  fullWidth
                  multiline
                  rows={3}
                  sx={{ '& .MuiOutlinedInput-notchedOutline': { borderColor: '#B399D6' } }}
                />
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions sx={{ padding: 2 }}>
            <Button onClick={handleCloseDialog} variant="outlined" sx={{ color: '#B399D6', borderColor: '#B399D6' }}>
              Annuler
            </Button>
            <Button type="submit" variant="contained" sx={{ bgcolor: '#B399D6', '&:hover': { bgcolor: '#A283C6' } }}>
              {isEditing ? 'Enregistrer' : 'Ajouter'}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      {/* Dialog de confirmation de suppression */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        maxWidth="xs"
        fullWidth
      >
        <DialogTitle sx={{ bgcolor: '#EF5350', color: '#fff' }}>
          Confirmation de suppression
        </DialogTitle>
        <DialogContent dividers>
          <DialogContentText>
            Êtes-vous sûr de vouloir supprimer l'évaluation <strong>{selectedEvaluation?.titre}</strong> ?<br />
            Cette action est irréversible.
          </DialogContentText>
        </DialogContent>
        <DialogActions sx={{ padding: 2 }}>
          <Button onClick={handleCloseDeleteDialog} sx={{ color: '#333' }}>
            Annuler
          </Button>
          <Button 
            onClick={handleDeleteEvaluation} 
            variant="contained" 
            color="error"
            sx={{ bgcolor: '#EF5350', '&:hover': { bgcolor: '#E53935' } }}
          >
            Supprimer
          </Button>
        </DialogActions>
      </Dialog>
    </CustomContainer>
  );
};

export default EvaluationList;
