import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, 
  Typography, 
  CircularProgress, 
  Alert,
  Container,
  Paper,
  Button,
  TextField,
  Card,
  CardContent,
  Grid,
  IconButton,
  Tooltip,
  useMediaQuery,
  useTheme
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { Timeline, TimelineItem, TimelineSeparator, TimelineConnector, TimelineContent, TimelineDot, TimelineOppositeContent } from '@mui/lab';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import SchoolIcon from '@mui/icons-material/School';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import DateRangeIcon from '@mui/icons-material/DateRange';
import LaunchIcon from '@mui/icons-material/Launch';
import api from '../api/axios';

// Stylisation personnalisée
const CustomContainer = styled(Container)(({ theme }) => ({
  padding: theme.spacing(3),
}));

const CustomPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
  borderRadius: '12px',
  background: '#fff',
  marginBottom: theme.spacing(4),
  borderTop: '4px solid #B399D6',
}));

const DateSelector = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
  borderRadius: '12px',
  background: '#fff',
  marginBottom: theme.spacing(4),
  display: 'flex',
  flexDirection: 'column',
  [theme.breakpoints.up('sm')]: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  '& .MuiTextField-root': {
    marginBottom: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      marginBottom: 0,
      marginRight: theme.spacing(3),
    },
  },
}));

const StyledButton = styled(Button)(({ theme, variant }) => ({
  borderRadius: '8px',
  padding: variant === 'contained' ? '8px 24px' : '7px 24px',
  fontWeight: 'bold',
  textTransform: 'none',
  boxShadow: variant === 'contained' ? '0 4px 10px rgba(179, 153, 214, 0.3)' : 'none',
  backgroundColor: variant === 'contained' ? '#B399D6' : 'transparent',
  borderColor: variant === 'outlined' ? '#B399D6' : 'transparent',
  color: variant === 'contained' ? '#fff' : '#B399D6',
  '&:hover': {
    backgroundColor: variant === 'contained' ? '#A283C6' : 'rgba(179, 153, 214, 0.1)',
    borderColor: variant === 'outlined' ? '#A283C6' : 'transparent',
    boxShadow: variant === 'contained' ? '0 6px 12px rgba(179, 153, 214, 0.4)' : 'none',
  },
}));

const FormationCard = styled(Card)(({ theme }) => ({
  borderRadius: '12px',
  boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
  transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
  overflow: 'hidden',
  height: '100%',
  '&:hover': {
    transform: 'translateY(-4px)',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
  },
}));

const CardHeader = styled(Box)(({ theme }) => ({
  padding: theme.spacing(2),
  backgroundColor: '#B399D6',
  color: '#fff',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
}));

const CardHeaderText = styled(Typography)(({ theme }) => ({
  fontWeight: 'bold',
  fontSize: '1.1rem',
}));

const CardTimeTag = styled(Box)(({ theme }) => ({
  backgroundColor: 'rgba(255, 255, 255, 0.2)',
  padding: theme.spacing(0.5, 1.5),
  borderRadius: '100px',
  fontWeight: 'bold',
  fontSize: '0.85rem',
  display: 'flex',
  alignItems: 'center',
  '& .MuiSvgIcon-root': {
    fontSize: '0.85rem',
    marginRight: theme.spacing(0.5),
  },
}));

const Planning = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  
  const [selectedDate, setSelectedDate] = useState(() => {
    // Initialiser avec la date d'aujourd'hui au format YYYY-MM-DD
    const today = new Date();
    return today.toISOString().split('T')[0];
  });
  
  const [formations, setFormations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFormations(selectedDate);
  }, [selectedDate]);

  const fetchFormations = async (date) => {
    try {
      setLoading(true);
      setError(null);
      
      // Dans un vrai projet, appel API avec la date sélectionnée
      // const response = await api.get(`/formateur/planning?date=${date}`);
      
      // Simulation des données pour la démo
      const mockData = [
        {
          id: 1,
          titre: "Introduction JavaScript",
          lieu: "Salle A",
          heure_debut: "09:00",
          heure_fin: "12:00",
          formation_id: 1
        },
        {
          id: 2,
          titre: "React Components",
          lieu: "Salle B",
          heure_debut: "14:00",
          heure_fin: "17:30",
          formation_id: 2
        },
        {
          id: 3,
          titre: "Architecture MVC",
          lieu: "Amphi C",
          heure_debut: "10:30",
          heure_fin: "12:30",
          formation_id: 3
        }
      ];
      
      // Tri par heure de début
      const sortedData = [...mockData].sort((a, b) => {
        return a.heure_debut.localeCompare(b.heure_debut);
      });
      
      setFormations(sortedData);
    } catch (error) {
      console.error("Error fetching planning:", error);
      setError(`Erreur: ${error.response?.data?.message || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
  };

  // Formatter l'heure pour affichage
  const formatTime = (time) => {
    return time.replace(":", "h");
  };
  
  // Calculer la durée en minutes
  const calculateDuration = (start, end) => {
    const [startHour, startMin] = start.split(':').map(Number);
    const [endHour, endMin] = end.split(':').map(Number);
    
    const startMinutes = startHour * 60 + startMin;
    const endMinutes = endHour * 60 + endMin;
    
    return endMinutes - startMinutes;
  };

  if (loading) return (
    <Box textAlign="center" mt={4} sx={{ color: '#B399D6' }}>
      <CircularProgress sx={{ color: '#B399D6' }} />
      <Typography mt={2}>Chargement du planning...</Typography>
    </Box>
  );
  
  if (error) return (
    <Box mt={4} p={2}>
      <Alert severity="error">{error}</Alert>
      <StyledButton variant="contained" onClick={() => fetchFormations(selectedDate)} sx={{ mt: 2 }}>
        Réessayer
      </StyledButton>
    </Box>
  );

  // Formatter la date pour l'affichage
  const formatDate = (dateString) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('fr-FR', options);
  };

  return (
    <CustomContainer maxWidth="lg">
      <Box mb={4} mt={2}>
        <StyledButton 
          variant="outlined" 
          startIcon={<ArrowBackIcon />} 
          component={Link} 
          to="/formateur-animateur"
          sx={{ mb: 2 }}
        >
          Retour au profil
        </StyledButton>
        
        <Typography variant="h4" component="h1" gutterBottom sx={{ color: '#333', fontWeight: 'bold' }}>
          Planning des formations
        </Typography>
      </Box>

      {/* Sélecteur de date */}
      <DateSelector elevation={0}>
        <Box display="flex" alignItems="center" flexGrow={1}>
          <DateRangeIcon sx={{ color: '#B399D6', mr: 2, fontSize: 28 }} />
          <TextField
            id="date"
            label="Sélectionner une date"
            type="date"
            value={selectedDate}
            onChange={handleDateChange}
            InputLabelProps={{
              shrink: true,
            }}
            sx={{ 
              minWidth: 200,
              '& .MuiOutlinedInput-notchedOutline': { 
                borderColor: '#B399D6',
                borderRadius: '8px'
              },
              '& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline': {
                borderColor: '#B399D6'
              },
              '& .MuiInputLabel-root': {
                color: '#B399D6'
              }
            }}
          />
        </Box>
        <Typography variant="h6" color="#B399D6" fontWeight="bold">
          {formatDate(selectedDate)}
        </Typography>
      </DateSelector>

      {/* Liste des formations */}
      <CustomPaper>
        {formations.length === 0 ? (
          <Alert severity="info" sx={{ backgroundColor: 'rgba(179, 153, 214, 0.1)', color: '#B399D6' }}>
            Aucune formation programmée pour cette date.
          </Alert>
        ) : (
          <>
            <Typography variant="h5" component="h2" gutterBottom sx={{ color: '#B399D6', fontWeight: 'bold', mb: 3 }}>
              Formations du jour
            </Typography>
            
            {/* Vue chronologique */}
            <Box sx={{ display: { xs: 'none', md: 'block' }, mb: 6 }}>
              <Timeline position="alternate">
                {formations.map((formation) => (
                  <TimelineItem key={formation.id}>
                    <TimelineOppositeContent color="#666">
                      <Typography variant="body2" fontWeight="bold">
                        {formatTime(formation.heure_debut)} - {formatTime(formation.heure_fin)}
                      </Typography>
                      <Typography variant="caption">
                        {calculateDuration(formation.heure_debut, formation.heure_fin)} min
                      </Typography>
                    </TimelineOppositeContent>
                    <TimelineSeparator>
                      <TimelineDot sx={{ bgcolor: '#B399D6' }}>
                        <SchoolIcon />
                      </TimelineDot>
                      <TimelineConnector sx={{ bgcolor: '#B399D6' }} />
                    </TimelineSeparator>
                    <TimelineContent>
                      <Paper elevation={1} sx={{ p: 2, backgroundColor: '#f8f6fb', borderRadius: '12px', borderLeft: '4px solid #B399D6' }}>
                        <Typography variant="h6" component="h3" fontWeight="bold" color="#333">
                          {formation.titre}
                        </Typography>
                        <Box display="flex" alignItems="center" mt={1}>
                          <LocationOnIcon fontSize="small" sx={{ color: '#B399D6' }} />
                          <Typography variant="body2" sx={{ ml: 1 }}>
                            {formation.lieu}
                          </Typography>
                        </Box>
                        <Box mt={1}>
                          <Button
                            variant="text"
                            size="small"
                            component={Link}
                            to={`/formations/${formation.formation_id}`}
                            sx={{ color: '#B399D6', fontWeight: 'bold', p: 0, mt: 1 }}
                            endIcon={<LaunchIcon fontSize="small" />}
                          >
                            Voir les détails
                          </Button>
                        </Box>
                      </Paper>
                    </TimelineContent>
                  </TimelineItem>
                ))}
              </Timeline>
            </Box>

            {/* Vue en cartes */}
            <Grid container spacing={3}>
              {formations.map((formation) => (
                <Grid item xs={12} sm={6} md={4} key={`card-${formation.id}`}>
                  <FormationCard>
                    <CardHeader>
                      <CardHeaderText>
                        {formation.titre}
                      </CardHeaderText>
                      <CardTimeTag>
                        <AccessTimeIcon />
                        {formation.heure_debut} - {formation.heure_fin}
                      </CardTimeTag>
                    </CardHeader>
                    <CardContent>
                      <Box display="flex" alignItems="center" mb={2}>
                        <LocationOnIcon fontSize="small" sx={{ color: '#B399D6', mr: 1 }} />
                        <Typography variant="body2" fontWeight="medium">
                          {formation.lieu}
                        </Typography>
                      </Box>
                      
                      <Box display="flex" alignItems="center" mb={3}>
                        <AccessTimeIcon fontSize="small" sx={{ color: '#B399D6', mr: 1 }} />
                        <Typography variant="body2">
                          {calculateDuration(formation.heure_debut, formation.heure_fin)} minutes
                        </Typography>
                      </Box>
                      
                      <StyledButton
                        variant="outlined"
                        size="small"
                        component={Link}
                        to={`/formations/${formation.formation_id}`}
                        fullWidth
                      >
                        Voir les détails
                      </StyledButton>
                    </CardContent>
                  </FormationCard>
                </Grid>
              ))}
            </Grid>
          </>
        )}
      </CustomPaper>
    </CustomContainer>
  );
};

export default Planning;
